CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `karyawan_salman` AS select row_number() OVER `w` AS `No`,`karyawan`.`nip` AS `Nip`,`karyawan`.`nama` AS `Nama`,`karyawan`.`alamat` AS `Alamat`,(case when (`karyawan`.`gend` = 'L') then 'Laki-Laki' when (`karyawan`.`gend` = 'P') then 'Perempuan' end) AS `Gender`,date_format(`karyawan`.`tgl_lahir`,'%d %m %Y') AS `Tanggal Lahir` from `karyawan` window `w` AS (ORDER BY `karyawan`.`insert_at` )